definition.script:
pipeline {
  agent any
     environment {
       MONGO_IP='127.0.0.1'
       MONGO_USER='mongoadmin'
       MONGO_PASSWORD='mongoadmin'
       MONGO_PORT='3002'
    }
  stages {    
    stage('Cloning Git') {
      steps {
        git 'https://github.com/ashwinrisbood/checkbox.io.git'
      }
    }

    stage('Install dependencies') {

      steps {
        dir("server-side/site/") {
          sh 'npm install'
      }
    }
  }
   stage('build') {
        steps {
                dir("server-side/site/") {
                         sh 'npm build'
                }
        }
      }
 }
}
