definition.script:
pipeline{
  agent any
  stages{
    stage('Clone repository'){
     steps {
        git([url:'root@134.209.115.128:/Project/ansible-srv/iTrust2/iTrust2-v4',branch:'master',credentialsId:'mykey'])
      }
    }
    stage ('Build project') {
      steps{
        dir('iTrust2'){
          sh 'mvn -f pom-data.xml process-test-classes'
          step( [ $class: 'JacocoPublisher',
                classPattern: 'target/classes',
                sourcePattern: 'src/main/java',
                exclusionPattern: 'src/test*'
              ] )
        }       
        }
    }
    stage('Test Build'){
      steps{
        dir('iTrust2'){
          sh 'mvn clean test checkstyle:checkstyle findbugs:findbugs'
          step([$class: 'CheckStylePublisher',
                  canRunOnFailed: true,
                  pattern: '**/target/checkstyle-result.xml',
                  useStableBuildAsReference: true
                ])
          step( [ $class: 'FindBugsPublisher',
                  canRunOnFailed: true,
                  pattern: '**/target/findbugsXml.xml',
                  useStableBuildAsReference: true,
                  canComputeNew: false, 
                  healthy: '50', 
                  unHealthy: '50'
              ] )
        }
      }
    }
  }

}
